import React, { useState } from 'react';
import './App.css';

function App() {
  const [userName, setUserName] = useState('');
  const logoUrl = "https://via.placeholder.com/150x50.png?text=BridgeLabz+Logo"; // Replace with actual logo URL

  const handleLogoClick = () => {
    window.open("https://www.bridgelabz.com", "_blank");
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Hello from BridgeLabz{userName ? `, ${userName}` : ''}</h1>
        <input
          type="text"
          value={userName}
          onChange={(e) => setUserName(e.target.value)}
          placeholder="Enter your name"
          className="input"
        />
        <img
          src={logoUrl}
          alt="BridgeLabz Logo"
          className="logo"
          onClick={handleLogoClick}
        />
      </header>
    </div>
  );
}

export default App;
