import React, { useState } from 'react';
import './payroll-form.css';
import EmployeeService from '../../services/EmployeeService';

const PayrollForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    profileImage: null, // Changed to store the uploaded file
    gender: '',
    department: [],
    salary: '',
    startDate: { day: '1', month: 'January', year: '2020' },
    notes: ''
  });

  const departments = ['HR', 'Sales', 'Finance', 'Engineer', 'Others'];
  const days = Array.from({ length: 31 }, (_, i) => (i + 1).toString());
  const months = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];
  const years = Array.from({ length: 26 }, (_, i) => (2025 - i).toString());

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file); // Create a URL for preview
      setFormData({ ...formData, profileImage: imageUrl });
    }
  };

  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prev) => {
      const updatedDepts = checked
        ? [...prev.department, value]
        : prev.department.filter((dept) => dept !== value);
      return { ...prev, department: updatedDepts };
    });
  };

  const handleDateChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      startDate: { ...prev.startDate, [name]: value }
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const employeeData = {
      name: formData.name,
      profileImage: formData.profileImage, // This will be the image URL or file
      gender: formData.gender,
      department: formData.department,
      salary: formData.salary,
      startDate: `${formData.startDate.day} ${formData.startDate.month} ${formData.startDate.year}`,
      notes: formData.notes
    };
    EmployeeService.addEmployee(employeeData)
      .then(() => {
        alert('Employee added successfully!');
        handleReset();
      })
      .catch((error) => {
        console.error('Error adding employee:', error);
        alert('Failed to add employee.');
      });
  };

  const handleReset = () => {
    setFormData({
      name: '',
      profileImage: null,
      gender: '',
      department: [],
      salary: '',
      startDate: { day: '1', month: 'January', year: '2020' },
      notes: ''
    });
  };

  return (
    <div className="payroll-form-container">
      <h2>Employee Payroll Form</h2>
      <div className="form-content">
        <div className="form-field">
          <label>Name</label>
          <input
            type="text"
            name="name"
            placeholder="Your name..."
            value={formData.name}
            onChange={handleInputChange}
          />
        </div>

        <div className="form-field">
          <label>Profile Image</label>
          <input
            type="file"
            name="profileImage"
            accept="image/*"
            onChange={handleImageUpload}
          />
          {formData.profileImage && (
            <div className="image-preview">
              <img src={formData.profileImage} alt="Profile Preview" />
            </div>
          )}
        </div>

        <div className="form-field">
          <label>Gender</label>
          <div className="radio-group">
            <label>
              <input
                type="radio"
                name="gender"
                value="Male"
                checked={formData.gender === 'Male'}
                onChange={handleInputChange}
              />
              Male
            </label>
            <label>
              <input
                type="radio"
                name="gender"
                value="Female"
                checked={formData.gender === 'Female'}
                onChange={handleInputChange}
              />
              Female
            </label>
          </div>
        </div>

        <div className="form-field">
          <label>Department</label>
          <div className="checkbox-group">
            {departments.map((dept) => (
              <label key={dept}>
                <input
                  type="checkbox"
                  name="department"
                  value={dept}
                  checked={formData.department.includes(dept)}
                  onChange={handleCheckboxChange}
                />
                {dept}
              </label>
            ))}
          </div>
        </div>

        <div className="form-field">
          <label>Salary</label>
          <input
            type="number"
            name="salary"
            placeholder="Salary"
            value={formData.salary}
            onChange={handleInputChange}
          />
        </div>

        <div className="form-field">
          <label>Start Date</label>
          <div className="date-group">
            <select
              name="day"
              value={formData.startDate.day}
              onChange={handleDateChange}
            >
              {days.map((day) => (
                <option key={day} value={day}>{day}</option>
              ))}
            </select>
            <select
              name="month"
              value={formData.startDate.month}
              onChange={handleDateChange}
            >
              {months.map((month) => (
                <option key={month} value={month}>{month}</option>
              ))}
            </select>
            <select
              name="year"
              value={formData.startDate.year}
              onChange={handleDateChange}
            >
              {years.map((year) => (
                <option key={year} value={year}>{year}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="form-field">
          <label>Notes</label>
          <textarea
            name="notes"
            value={formData.notes}
            onChange={handleInputChange}
          />
        </div>

        <div className="form-actions">
          <button type="button" onClick={handleReset}>Cancel</button>
          <button type="button" onClick={handleSubmit}>Submit</button>
          <button type="button" onClick={handleReset}>Reset</button>
        </div>
      </div>
    </div>
  );
};

export default PayrollForm;
